#include <iostream>
#include <iomanip>
using namespace std; 

int main(){
    int height=0;
    cout<<"Enter the height:"<<endl;
    cin>>height;

    string alphabet="";
    for(int i = height; i>0;i--){
        string layer = alphabet.substr(0,i);
        cout<<layer<<endl;
        alphabet=alphabet.substr(i);
    }
    return 0;
}